eval("__webpack_require__.r(__webpack_exports__);\n/* harmony export */ __webpack_require__.d(__webpack_exports__, {\n/* harmony export */   BookController: () => (/* binding */ BookController)\n/* harmony export */ });\nclass BookController {\n    constructor(bookService, bookView) {\n        this.bookService = bookService;\n        this.bookView = bookView;\n        this.init();\n    }\n    init() {\n        this.renderBooks();\n        this.bookView.setBookHandler(this.handleSaveBook.bind(this));\n    }\n    renderBooks() {\n        const books = this.bookService.getAll();\n        this.bookView.renderBooks(books, this.handleRowClick.bind(this), this.handleDeleteBook.bind(this));\n    }\n    handleRowClick(book) {\n        this.bookView.populateForm(book);\n    }\n    handleSaveBook(updatedBook) {\n        const existingBook = this.bookService.getById(updatedBook.id);\n        if (existingBook) {\n            this.bookService.update(updatedBook.id, updatedBook);\n        }\n        else {\n            this.bookService.create(updatedBook);\n        }\n        this.renderBooks();\n    }\n    handleDeleteBook(bookId) {\n        const isDeleted = this.bookService.delete(bookId);\n        if (isDeleted) {\n            this.renderBooks();\n        }\n        else {\n            alert(\"Failed to delete the book.\");\n        }\n    }\n}\n\n\n//# sourceURL=webpack:///./src/controllers/book.controller.ts?");